import React, { useState, useEffect } from 'react';

function HomeScreen() {

  const [subProducts, setsubProducts] = useState([]);
  const [selectedProduct, setSelectedCategory] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3000/products');
      const data = await response.json();
      setProducts(data[0]);
    }

    fetchData();}, []);


    function handleCategoryClick(id) {
      setSelectedCategory(id);
      async function fetchsubData() {
        const response = await fetch('http://localhost:3000/products/'+ id);
        const data = await response.json();
        setsubProducts(data[0]);
      }

      fetchsubData();
      setsubProducts([]);

    }

return (
<div>
<h1>Ürünler</h1>
<table>
<thead>
<tr>
<th>Ürün Adı</th>
</tr>
{products.map(category => (
    <li key={category.id}>
      <button onClick={() => handleCategoryClick(category.id)}>
        {category.name}
      </button>
      {/* Sadece seçilen kategorinin alt kategorilerini gösterin */}
      {category.id === selectedProduct && (
        <ul className="sub-Product">
          {subProducts.map(subCategory => (
            <li key={subCategory.id}>
              <button>{subCategory.name}</button>
            </li>
          ))}
        </ul>
      )}
    </li>
  ))}
</thead>
</table>
</div>
);
}
export default HomeScreen;
