import React, { useState } from 'react';

function SigninScreen(props) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  function handleSignClick(id) { 
    async function fetchpostSignData() {
     try {
       const response = await fetch('http://localhost:3000/guest/login', {
         method: 'POST',
         body: JSON.stringify({
            email: email,
            password: password
         }),

       });
       const data = await response.json();
       console.log(data);
     } catch (error) {
       console.error(error);
     }
   }
   fetchpostSignData();

}

  return (
    <div className="form">
    <form onSubmit={handleSignClick()}>
      <ul className='form-container'>
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={e => setEmail(e.target.value)}
      />
      <br />
      <label htmlFor="password">Şifre:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={e => setPassword(e.target.value)}
      />
      <br />
      <button type="submit" className="button primary">Giriş Yap</button>
      </ul>
    </form>
    </div>
  );
}

export default SigninScreen;
