import React, { useState, useEffect } from 'react';
import styled from 'styled-components';


function HomeScreen() {

  const [subProduct, setsubProducts] = useState([]);
  const [selectedProduct, setSelectedCategory] = useState('');
  const [products, setProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [user_id, setUserId] = useState('');

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3000/products');
      const data = await response.json();
      setProducts(data[0]);
    }

    fetchData();}, []);
  function handleCategoryClick(id) {
      setSelectedCategory(id);
      async function fetchsubData() {
        const response = await fetch('http://localhost:3000/products/'+ id);
        const data = await response.json();
        setsubProducts(data[0]);
      }
      fetchsubData();
    setsubProducts([]);}
  function handleReviewClick(id) { setSelectedCategory(id);
          async function fetchreviewData() {
          const response = await fetch('http://localhost:3000/reviews/'+ id);
          const data = await response.json();
    setReviews(data[0]); }fetchreviewData();setReviews([]); }
  function handlePostReviewClick(id) { 
       setSelectedCategory(id);
       async function fetchpostreviewData() {
        try {
          const response = await fetch('http://localhost:3000/reviews/addreview/', {
            method: 'POST',
            body: JSON.stringify({
              product_id: id,
              user_id: user_id,
              comment: comment,
              rating: rating
            }),
            headers: {
              'Content-Type': 'application/json'
            }
          });
          const data = await response.json();
          console.log(data);
        } catch (error) {
          console.error(error);
        }
      }
        fetchpostreviewData();
        setReviews([]);
  }
return (
<div>
  <h1>Ürünler</h1>
  <table>
    <thead>
      <TableRow>
        <th>Ürün Adı</th>
      </TableRow>
  <TableRow>
    {products.map(product => (
    <li key={product.id}>
      <button onClick={() => { handleCategoryClick(product.id); handleReviewClick(product.id); }}>
        {product.name}
      </button>
      {/* Sadece seçilen kategorinin alt kategorilerini gösterin */}
      {product.id === selectedProduct && (
        <ul className="sub-product">
           <TableContainer>
          <Table>
            <TableHead>
    <TableRow>
      <th>Ürün Adı</th>
      <th>Fiyat</th>
      <th>Boyut</th>
      <th>Renk</th>
      <th>Stok</th>
      <th>Görsel</th>
    </TableRow>
  </TableHead>
  <tbody>
    {subProduct.map(subProducts => (
      <TableRow key={subProducts.id}>
        <TableCell>{subProducts.name}</TableCell>
        <TableCell>{subProducts.price}</TableCell>
        <TableCell>{subProducts.size}</TableCell>
        <TableCell>{subProducts.color}</TableCell>
        <TableCell>{subProducts.stock}</TableCell>
        <TableCell>{subProducts.image}</TableCell>
      </TableRow>
    ))}
  </tbody>
</Table>
</TableContainer>
{reviews.map(review => (
            <li key={review.id}>
              <TableRow>
              <TableCell>{review.comment}</TableCell>
              <TableCell>{review.rating}</TableCell>
              </TableRow>
            </li>
          ))}{ <li>
            <TableRow>
              <TableCell>
                <form onSubmit={() => { handlePostReviewClick(product.id); }}>
                  <ul className="form-container">
                    <li>
                      <label htmlFor="rating">Rating</label>
                      <input
                        type="text"
                        name="rating"
                        id="rating"
                        onChange={e => setRating(e.target.value)}
                      ></input>
                    </li>
                    <li>
                      <label htmlFor="comment">Comment</label>
                      <input
                        type="text"
                        name="comment"
                        id="comment"
                        onChange={e => setComment(e.target.value)}
                      ></input>
                    </li>
                    <li>
                      <label htmlFor="user_id">User Id</label>
                      <input
                        type="text"
                        name="user_id"
                        id="user_id"
                        onChange={e => setUserId(e.target.value)}
                      ></input>
                    </li>
                    <li>
                      <button type="submit" className="button primary">
                        Post Review
                      </button>
                    </li>
                  </ul>
                </form>
              </TableCell>
            </TableRow>
          </li>}
        </ul>
      )}
    </li>
  ))}
  </TableRow>
</thead>
</table>
</div>
);
}
export default HomeScreen;


const TableContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 80%;
  margin: 0 auto;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
`;

const TableHead = styled.thead`
  background-color: #333;
  color: #fff;
`;

const TableRow = styled.TableRow`
  border-bottom: 1px solid #ccc;
`;

const TableCell = styled.TableCell`
  padding: 10px;
  text-align: left;
`;
