import React, { useState, useEffect } from 'react';
import Cookie from 'js-cookie';
function Myaddresseswallets() {

    const [addresses, setAddresses] = useState([]);
    const [wallets, setWallets] = useState([]);

    useEffect(() => {
        async function fetchData() {
            const userInfo = JSON.parse(Cookie.get('userInfo'));
            const response = await fetch('http://localhost:3000/myAddresses',{
                method: 'POST',
                body: JSON.stringify({
                    user_id: userInfo[0][0].id
                }),
                headers: {
                    'Content-Type': 'application/json'
                    
            }
            });
            const data = await response.json();
            setAddresses(data[0]);
            console.log(data[0], " addreses")
            const response2 = await fetch('http://localhost:3000/myWallets',{
                method: 'POST',
                body: JSON.stringify({
                    user_id: userInfo[0][0].id
                }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            const data2 = await response2.json();
            setWallets(data2[0]);
            console.log(data2[0], " wallets")
        }
        fetchData();
    }, []);

    function deleteaddress(id) {
        async function fetchdeleteData() {
            const response = await fetch('http://localhost:3000/addresses/deleteAddresses/', {
                method: 'DELETE',
                body: JSON.stringify({
                    id: id
                }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            if (response.ok) {
                console.log("deleted");
            }
            else
                console.log("error");
        }
        fetchdeleteData();
        setAddresses([]);
    }
    return (
        <div>
            <h1>Adresler</h1>
             <table>
                <thead>
                    <tr>
                        <th>Adres</th>
                        <th>Şehir</th>
                        <th>Sokak</th>
                        <th>Posta Kodu</th>
                    </tr>
                </thead>
                <tbody>
                    {addresses.map((address) => (
                        <tr key={address.id}>
                            <td>{address.address}</td>
                            <td>{address.city}</td>
                            <td>{address.street}</td>
                            <td>{address.zipcode}</td>
                            <td><button onClick={() => deleteaddress(address.id)}>Sil</button></td>
                        </tr>
                    ))}
                </tbody>
                <h1>Cüzdanlar</h1>
                <thead>
                    <tr>
                        <th>Cüzdan Adi</th>
                        <th>Kart Numarası</th>
                    </tr>
                </thead>
                <tbody>
                    {wallets.map((wallet) => (
                        <tr key={wallet.id}>
                            <td>{wallet.card_name}</td>
                            <td>{wallet.card_number}</td>
                        </tr>
                    ))}
                </tbody>
                            
             </table>
        </div>
    )
}

export default Myaddresseswallets;
