import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
function HomeScreen(props) {
  const [products, setProducts] = useState([]);
  const [category, setCategory] = useState('');
  const [barcode, setBarcode] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(false);

  async function fetchProducts(category, barcode) {
    const response = await fetch(`http://localhost:3000/products/category-barcode?category=${category}&barcode=${barcode}`);
    const products = await response.json();
    console.log(products);
  }



  const submitHandler = (e) => {
    e.preventDefault();
    dispatch(fetchProducts(category, barcode ));
  };


  return (
    <>
      {category && <h2>{category}</h2>}

      <ul className="filter">
        <li>
          <form onSubmit={submitHandler}>
            <input
              name="searchKeyword"
              onChange={(e) => setBarcode(e.target.value)}
            />
            <button type="submit">Search</button>
          </form>
        </li>

      </ul>
      {loading ? (
        <div>Loading...</div>
      ) : error ? (
        <div>{error}</div>
      ) : (
        <ul className="products">
          {products.map((product) => (
            <li key={product._id}>
              <div className="product">
                <Link to={'/product/' + product._id}>
                  <img
                    className="product-image"
                    src={product.image}
                    alt="product"
                  />
                </Link>
                <div className="product-name">
                  <Link to={'/product/' + product._id}>{product.name}</Link>
                </div>
                <div className="product-stock">{product.stock}</div>
                <div className="product-price">${product.price}</div>

              </div>
            </li>
          ))}
        </ul>
      )}
    </>
  );
}
export default HomeScreen;
