import React, { useState, useEffect } from 'react';

function HomeScreen() {

  const [subProduct, setsubProducts] = useState([]);
  const [selectedProduct, setSelectedCategory] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3000/products');
      const data = await response.json();
      setProducts(data[0]);
    }

    fetchData();}, []);

    function handleCategoryClick(id) {
      setSelectedCategory(id);
      async function fetchsubData() {
        const response = await fetch('http://localhost:3000/products/'+ id);
        const data = await response.json();
        setsubProducts(data[0]);
      }

      fetchsubData();
      setsubProducts([]);

    }


return (
<div>
  <h1>Ürünler</h1>
  <table>
    <thead>
      <tr>
        <th>Ürün Adı</th>
      </tr>
  <tr>
    {products.map(product => (
    <li key={product.id}>
      <button onClick={() => handleCategoryClick(product.id)}>
        {product.name}
      </button>
      {/* Sadece seçilen kategorinin alt kategorilerini gösterin */}
{product.id === selectedProduct && (
<ul className="sub-product">
{subProduct.map(subProducts => (
<li key={subProducts.id}>
{subProducts.comment === null ? (
      <tr>
      <td>Ürün adi: {subProducts.name}</td>
      <td>Ürün fiyati: {subProducts.price}</td>
      <td>Ürün size: {subProducts.size}</td>
      <td>Ürün renk: {subProducts.color}</td>
      <td>Ürün stock: {subProducts.stock}</td>
      <td>Ürün barcode: {subProducts.barcode}</td>
      <td>Ürün image: {subProducts.image}</td>
      </tr>
    ) : (
      <tr>
      <td>Ürün adi: {subProducts.name}</td>
      <td>Ürün fiyati: {subProducts.price}</td>
      <td>Ürün size: {subProducts.size}</td>
      <td>Ürün renk: {subProducts.color}</td>
      <td>Ürün stock: {subProducts.stock}</td>
      <td>Ürün barcode: {subProducts.barcode}</td>
      <td>Ürün image: {subProducts.image}</td>
      <td>Ürün yorum: {subProducts.comment}</td>
      <td>Ürün rating: {subProducts.rating}</td>
      </tr>
    )}
</li>
))}
</ul>
)}
</li>
))}
</tr>
</thead>
</table>
</div>
);
}
export default HomeScreen;
