import React, { useEffect, useState } from 'react';
import Cookie from 'js-cookie';
function addresswalletsScreen() {
    const [address, setAddress] = useState([]);
    const [wallet, setWallet] = useState([]);

    function handleAddressClick() {
        async function fetchAddressData() {
            const response = await fetch('http://localhost:3000/address');
            const data = await response.json();
            setAddress(data[0]);
        }
        fetchAddressData();
    }
    
    

  return (
    <div>
        <h1>My Addresses</h1>
        <button onClick={handleAddressClick}>Get Addresses
        <table>
            <tr>
                <th>Address</th>
                <th>City</th>
                <th>Street</th>
                <th>Zipcode</th>
            </tr>
            {
                address.map((item) => (
                    <tr>
                        <td>{item.address}</td>
                        <td>{item.city}</td>
                        <td>{item.street}</td>
                        <td>{item.zipcode}</td>
                    </tr>
                ))}
       
        </table>
        </button>
        <br/>
        <br/>
        <h1>My Wallets</h1>
        <table>
            <tr>
                <th>Wallet Name</th>
                <th>Wallet Balance</th>
            </tr>
            <tr>
                <td>Wallet Name</td>
                <td>Wallet Balance</td>
            </tr>
            </table>
            </div>
  );
}
export default addresswalletsScreen;
