import React  from 'react';
import Cookie from 'js-cookie';
function addresswalletsScreen() {


    async function fetchData() {
        const userInfo = JSON.parse(Cookie.get('userInfo'));
        const response = await fetch('http://localhost:3000/myaddresses', {
            method: 'POST',
            body: JSON.stringify({ 
                user_id: userInfo[0][0].id,
            }),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const data = await response.json();
        console.log(data[0]);
        const response2 = await fetch('http://localhost:3000/mywallets', {
            method: 'POST',
            body: JSON.stringify({
                user_id: userInfo[0][0].id,
            }),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const data2 = await response2.json();
        console.log(data2[0]);
    }
    fetchData();
    
    

  return (
    <div>
        <h1>My Addresses</h1>
        <table>
            <tr>
                <th>Address</th>
                <th>City</th>
                <th>Street</th>
                <th>Zipcode</th>
            </tr>
       
        </table>
        <br/>
        <br/>
        <h1>My Wallets</h1>
        <table>
            <tr>
                <th>Wallet Name</th>
                <th>Wallet Balance</th>
            </tr>
            <tr>
                <td>Wallet Name</td>
                <td>Wallet Balance</td>
            </tr>
            </table>
            </div>
  );
}
export default addresswalletsScreen;
