import React, { useState } from 'react';
import Cookie from 'js-cookie';
function addresswalletsScreen() {
   
  const [mysAddresses, setmyAddresses] = useState([]);
    const [mysWallets, setmyWallets] = useState([]);

  function myAddresses(){
    async function fetchmyAddressesData() {
      const userInfo = JSON.parse(Cookie.get('userInfo'));
      const response = await fetch("http://localhost:3000/myAddresses", {
        method: 'POST',
        body: JSON.stringify({ 
          user_id: userInfo[0][0].id
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      setmyAddresses(data[0]);
    }
    fetchmyAddressesData();
  }

    function myWallets(){
        async function fetchmyWalletsData() {
            const userInfo = JSON.parse(Cookie.get('userInfo'));
            const response = await fetch("http://localhost:3000/myWallets", {
                method: 'POST',
                body: JSON.stringify({
                    user_id: userInfo[0][0].id
                }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            const data = await response.json();
            setmyWallets(data[0]);
        }
        fetchmyWalletsData();
    }


  return (
    <div>
        <h1>My Addresses</h1>
        <table>
            <tr>
                <th>Address</th>
                <th>City</th>
                <th>Street</th>
                <th>Zipcode</th>
            </tr>
        {mysAddresses.map((myAddresses) => (
            <tr>
                <td>{myAddresses.address}</td>
                <td>{myAddresses.city}</td>
                <td>{myAddresses.street}</td>
                <td>{myAddresses.zipcode}</td>    
            </tr>
        ))}

        </table>
       
        <br/>
        <br/>
        <h1>My Wallets</h1>
        <table>
            <tr>
                <th>Wallet Name</th>
                <th>Wallet Balance</th>
            </tr>
            <tr>
                <td>Wallet Name</td>
                <td>Wallet Balance</td>
            </tr>
            </table>
            </div>
  );
}
export default addresswalletsScreen;
