import React, { useState, useEffect } from 'react';

function HomeScreen() {

  const [subProduct, setsubProducts] = useState([]);
  const [selectedProduct, setSelectedCategory] = useState('');
  const [products, setProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3000/products');
      const data = await response.json();
      setProducts(data[0]);
    }

    fetchData();}, []);
  function handleCategoryClick(id) {
      setSelectedCategory(id);
      async function fetchsubData() {
        const response = await fetch('http://localhost:3000/products/'+ id);
        const data = await response.json();
        setsubProducts(data[0]);
      }
      fetchsubData();
      setsubProducts([]);}
  function handleReviewClick(id) {
        setSelectedCategory(id);
        async function fetchreviewData() {
          const response = await fetch('http://localhost:3000/reviews/'+ id);
          const data = await response.json();
          setReviews(data[0]);
        }
        fetchreviewData();
        setReviews([]); }
  async function handleSubmit(event) {
          event.preventDefault();

          try {
            const response = await fetch('http://localhost:3000/reviews/addreview/' + product_id, {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json'
              },
              body: JSON.stringify({ review: review, product_id: product_id })
            });
            const data = await response.json();
            console.log(data);
          } catch (error) {
            console.log(error);
          }}

return (
<div>
  <h1>Ürünler</h1>
  <table>
    <thead>
      <tr>
        <th>Ürün Adı</th>
      </tr>
  <tr>
    {products.map(product => (
    <li key={product.id}>
      <button onClick={() => { handleCategoryClick(product.id); handleReviewClick(product.id); }}>
        {product.name}
      </button>
      {/* Sadece seçilen kategorinin alt kategorilerini gösterin */}
      {product.id === selectedProduct && (
        <ul className="sub-product">
          {subProduct.map(subProducts => (
            <li key={subProducts.id}>
              <tr>
              <td>{subProducts.name}</td>
              <td>{subProducts.price}</td>
              <td>{subProducts.size}</td>
              <td>{subProducts.color}</td>
              <td>{subProducts.stock}</td>
              <td>{subProducts.image}</td>    
              </tr>
            </li>
          ))}{reviews.map(review => (
            <li key={review.id}>
              <tr>
              <td>{review.comment}</td>
              <td>{review.rating}</td>
              </tr>
            </li>
          ))}{
            <form onSubmit={handleSubmit}>
          <label>
            Name:
            <input type="text" name="name" value={name} onChange={handleChange} />
          </label>
          <br />
          <label>
            Review:
            <input type="text" name="review" value={review} onChange={handleChange} />
          </label>
          <br />
          <input type="hidden" name="product_id" value={product_id} />
          <input type="submit" value="Submit" />
        </form>
        }
        </ul>
      )}
    </li>
  ))}
  </tr>
</thead>
</table>
</div>
);
}
export default HomeScreen;
