import React, { useState, useEffect } from 'react';

function HomeScreen() {

  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3000/products');
      const data = await response.json();
      setProducts(data[0]);
    }

    fetchData();
  }, []);

  return (
    <div>
      <h1>Ürünler</h1>
      <table>
        <thead>
        <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Price</th>
            <th>Size</th>
            <th>Renk</th>
            <th>Stok</th>
            <th>İmage</th>
            <th>Barcode</th>
          </tr>
        </thead>
        <tbody>
          {products.map(product => (
            <tr key={product.id}>
              <td>{product.id}</td>
              <td>{product.name}</td>
              <td>{product.price} TL</td>
              <td>{product.size}</td>
              <td>{product.color}</td>
              <td>{product.stock}</td>

              <td>{product.barcode}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default HomeScreen;
