import React, { useState, useEffect } from 'react';

function HomeScreen() {

  const [modalOpen, setModalOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [products, setProducts] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://localhost:3000/products');
      const data = await response.json();
      setProducts(data[0]);
    }

    fetchData();
  }, []);

  function handleShowModal(productId) {
    // Ürün detaylarını çekin
    async function fetchProduct() {
      const response = await fetch('http://localhost:3000/products/' + productId);
      const product = await response.json();
      setSelectedProduct(product[0]);
    }
    fetchProduct();
  }

return (
<div>
<h1>Ürünler</h1>
<table>
<thead>
<tr>
<th>ID</th>
<th>Name</th>
<th>Price</th>
<th>Size</th>
<th>Renk</th>
<th>Stok</th>
<th>İmage</th>
<th>Barcode</th>
</tr>
</thead>
<tbody>
{products.map(product => (
<tr key={product.id}>
<td>{product.id}</td>
<td>
<button onClick={() => handleShowModal(product.id)}>
{product.name}
</button>
</td>
<td>{product.price} TL</td>
<td>{product.size}</td>
<td>{product.color}</td>
<td>{product.stock}</td>
<td>x</td>
<td>{product.barcode}</td>
</tr>
))}
</tbody>
</table>
{modalOpen && (
<div className="modal">
<h2>{selectedProduct.name}</h2>
<p>{selectedProduct.color}</p>
<p>Fiyat: {selectedProduct.price} TL</p>
<button onClick={() => setModalOpen(false)}>Kapat</button>
</div>
)}
</div>
);
}
export default HomeScreen;
