import React, { useState } from 'react';
import Cookie from 'js-cookie';
function addresswalletsScreen() {
   
  const [Addresses, setAddresses] = useState([]);
  const [Wallets, setWallets] = useState([]);

  function myAddresses(){
    async function fetchmyAddressesData() {
      const userInfo = JSON.parse(Cookie.get('userInfo'));
      const response = await fetch("http://localhost:3000/myAddresses", {
        method: 'POST',
        body: JSON.stringify({ 
          user_id: userInfo[0][0].id
        }),
        headers: {
          'Content-Type': 'application/json'
        }
      });
      const data = await response.json();
      setAddresses(data[0]);
    }
    fetchmyAddressesData();
  }

    function myWallets(){
        async function fetchmyWalletsData() {
            const userInfo = JSON.parse(Cookie.get('userInfo'));
            const response = await fetch("http://localhost:3000/myWallets", {
                method: 'POST',
                body: JSON.stringify({
                    user_id: userInfo[0][0].id
                }),
                headers: {
                    'Content-Type': 'application/json'
                }
            });
            const data = await response.json();
            setWallets(data[0]);
        }
        fetchmyWalletsData();
    }
  return (
    <div>
        <h1>My Addresses</h1>
        <button onClick={myAddresses}>
        <table>
            <tr>
                <th>Address</th>
                <th>City</th>
                <th>Street</th>
                <th>Zipcode</th>
            </tr>
        {Addresses.map((adres) => (
            <tr>
                <td>{adres.address}</td>
                <td>{adres.city}</td>
                <td>{adres.street}</td>
                <td>{adres.zipcode}</td>    
            </tr>
        ))}

        </table>
       </button>
        <br/>
        <br/>
        <h1>My Wallets</h1>
        <table>
            <tr>
                <th>Wallet Name</th>
                <th>Wallet Balance</th>
            </tr>
            <tr>
                <td>Wallet Name</td>
                <td>Wallet Balance</td>
            </tr>
            </table>
            </div>
  );
}
export default addresswalletsScreen;
