import React, { useState } from 'react';
import axios from 'axios';

function SigninScreen(props) {

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await axios.post('http://localhost:3000/guest/login', {
        email,
        password,
      });

      if (response.data.length > 0) {
        // Kullanıcı veritabanında bulundu, oturum açılıyor
        document.cookie = `user_id=${response.data[0].id}`;
      } else {
        // Kullanıcı veritabanında bulunamadı, hata mesajı göster
        alert('Geçersiz email veya şifre');
      }
    } catch (error) {
      console.error(error);
    }
  };
  console.log(document.cookie);
  console.log(handleSubmit);
  return (
    <div className="form">
    <form onSubmit={handleSubmit}>
      <ul className='form-container'>
      <label htmlFor="email">Email:</label>
      <input
        type="email"
        id="email"
        value={email}
        onChange={(event) => setEmail(event.target.value)}
      />
      <br />
      <label htmlFor="password">Şifre:</label>
      <input
        type="password"
        id="password"
        value={password}
        onChange={(event) => setPassword(event.target.value)}
      />
      <br />
      <button type="submit" className="button primary">Giriş Yap</button>
      </ul>
    </form>
    </div>
  );
}

export default SigninScreen;
